import { useEffect, useState } from "react";
import API from "../api";

export default function Events() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    API.get("/events")
      .then((res) => setEvents(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <div className="p-8" >
      <h1 className="text-3xl font-bold mb-4 text-white" style={{textAlign:"center", fontWidth:"bold"}}>GenVision Events</h1>
      <div className="row g-4">
        {events.map((c) => (
  <div key={c._id} className="col-6 col-md-4 col-lg-3">
<div className="event-card bg-white shadow"
  style={{
    margin:"5px",
    borderRadius:"10px",
    backgroundImage:`url(http://localhost:5000/api${c.image})`,
    backgroundSize:"contain",
    backgroundRepeat:"no-repeat",
    backgroundPosition:"center"
  }}
>
      {c.image && (
        <img
  src={`http://localhost:5000/api${c.image}`}
  alt={c.name}
  className="card-img-top img-fluid"
  style={{ width: "100%", height: "100%", objectFit: "contain", borderRadius:"10px"}}
/>

      )}
    </div>
  </div>
))}
      </div>
    </div>
  );
}
